import React, { Component } from "react";

class SearchBar extends Component {
  state = {
    search: ""
  };

  handleSearch = e => {
    setTimeout(
      this.setState({
        search: e.target.value
      })
    );
  };

  render() {
    return (
      <div className="search-bar">
        <input
          name="searchTerm"
          type="text"
          placeholder="search..."
          onChange={this.handleSearch}
        />
      </div>
    );
  }
}

export default SearchBar;
