import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import InfiniteScroll from "react-infinite-scroll-component";
import "./App.css";
import NavBar from "./components/NavBar";
import FunkosCardContainer from "./containers/FunkosCardContainer";
const API = "http://localhost:3000";

class App extends Component {
  state = {
    funkos: [].from({ length: 20 })
  };

  componentDidMount() {
    this.fetchFunkos();
  }

  ////// ------------- funkos request ------------------- /////////////

  fetchFunkos = () => {
    fetch(`${API}/funkos`)
      .then(resp => resp.json())
      .then(data => this.setState({ funkos: data }));
  };

  ////// ------------- infinite scroll ------------------- /////////////

  fetchMoreFunkos = () => {
    setTimeout(() => {
      this.setState({
        funkos: this.state.funkos.concat(this.state.funkos.from({ length: 20 }))
      });
    }, 1500);
  };

  ////// ------------- routes ------------------- /////////////

  home = () => {
    return <FunkosCardContainer funkos={this.state.funkos} />;
  };

  render() {
    return (
      <Router>
        <NavBar />
        <Route path="/" exact component={this.home} />
      </Router>
    );
  }
}

export default App;
