import React, { Component } from "react";

class SearchBar extends Component {
  state = {
    search: ""
  };

  handleSearch = e => {
    console.log(e.target.value);
    this.setState({
      search: e.target.value
    });
  };

  render() {
    return (
      <div className="search-bar">
        <form>
          <input
            name="searchTerm"
            type="text"
            placeholder="search..."
            onChange={this.handleSearch}
          />
        </form>
      </div>
    );
  }
}

export default SearchBar;
