import React, { Component } from "react";
import {
  BrowserRouter as Router,
  Route,
  Link,
  NavLink
} from "react-router-dom";
import logo from "../images/FunPop_logo.png";
class NavBar extends Component {
  render() {
    const toggleSingInOut = () => {
      return this.props.loggedIn ? (
        <div>
          <p> {`Welcome, ${this.props.username}`}</p>
          <button onClick={this.props.handleLogOut}>Log out</button>
        </div>
      ) : (
        <form>
          <label htmlFor="username">Username:</label>
          <input
            onChange={this.props.handleChange}
            id="username"
            type="text"
            name="username"
            value={this.props.username}
          />
          <label htmlFor="password">Password:</label>
          <input
            onChange={this.props.handleChange}
            id="password"
            type="password"
            name="password"
            value={this.props.password}
          />
          <button onClick={this.props.onLoginClicked}>Log in</button>
        </form>
      );
    };

    return (
      <div className="navbar">
        <ul className="topnav">
          <li>
            <img src={logo} alt="logo" className="logo" />
          </li>
          <li>
            <Link to="/">home</Link>
          </li>
          <li className="categories">
            <a className="dropbtn">categories</a>
            <div className="categories-content">
              <a value="Link 1">Link 1</a>
              <a value="Link 1">Link 2</a>
              <a value="Link 1">Link 3</a>
            </div>
          </li>
          <li>
            <div className="nav">
              {toggleSingInOut}
              {/* {this.props.logged_in ? (
