import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "./App.css";
import NavBar from "./components/NavBar";
import FunkosCardContainer from "./containers/FunkosCardContainer";
const API = "http://localhost:3000";

class App extends Component {
  state = {
    funkos: [],
    index: 0,
    loadingState: false
  };

  componentDidMount() {
    this.fetchFunkos();
  }

  ////// ------------- funkos request ------------------- /////////////

  fetchFunkos = () => {
    fetch(`${API}/funkos`)
      .then(resp => resp.json())
      .then(data => this.setState({ funkos: data }));
  };

  ////// ------------- infinite scroll ------------------- /////////////

  moreFunkos = () => {
    if (this.state.index === this.state.funkos.length - 20) {
      return;
    } else {
      this.setState({
        index: this.state.index + 20
      });
    }
  };

  displayFunkos() {
    let funkos = [];
    for (let i = 0; i < this.state.funkos; i++) {
      funkos.push(<li key={i}> Funko </li>);
    }
    return funkos;
  }

  loadMoreFunkos() {
    if (this.state.loadingState) {
      return;
    }
    this.setState({ loadingState: true });
    setTimeout(() => {
      this.setState;
    });
  }

  ////// ------------- routes ------------------- /////////////

  home = () => {
    return <FunkosCardContainer funkos={this.state.funkos} />;
  };

  render() {
    return (
      <Router>
        <NavBar />
        <Route path="/" exact component={this.home} />
      </Router>
    );
  }
}

export default App;
