import React, { Component } from "react";
import {
  BrowserRouter as Router,
  Route,
  Link,
  NavLink
} from "react-router-dom";
import api from "../util/api";

class OfferPage extends Component {
  state = {
    offer: [],
    ready: false
  };

  componentDidMount() {
    api.showOffer(this.props.id).then(data => {
      this.setState({ offer: data, ready: true });
    });
  }

  render() {
    const renderOffer = () => {
      const {
        name,
        image_url,
        fandom,
        category,
        release_date,
        exclusivity,
        trending_value,
        username,
        city,
        country
      } = this.state.offer;

      const parts = incoming_funko.funko.release_date.split("-");
      const date = new Date(parts[0], parts[1] - 1, parts[2]);

      return (
        <div className="make-an-offer-container">
          <div className="incoming-funko-container">
            <h1 className="info">{incoming_funko.funko.name}</h1>
            <div className="funko-image-div">
              <img
                src={incoming_funko.funko.image_url}
                alt={incoming_funko.funko.name}
                className="funko-page-img"
              />
              <div className="gradient" />
            </div>
            <div className="funko-incoming-details">
              <div>
                <h2 className="incoming-funko-topic">fandom:</h2>
                <h2 className="incoming-funko-info">
                  {incoming_funko.funko.fandom}
                </h2>
              </div>
              <div>
                <h2 className="incoming-funko-topic">category:</h2>
                <h2 className="incoming-funko-info">
                  {incoming_funko.funko.category}
                </h2>
              </div>
              <div>
                <h2 className="incoming-funko-topic">release date:</h2>
                <h2 className="incoming-funko-info">
                  {new Intl.DateTimeFormat("en-GB", {
                    year: "numeric",
                    month: "long",
                    day: "2-digit"
                  })
                    .format(date)
                    .toLowerCase()}
                </h2>
              </div>
              <div>
                <h2 className="incoming-funko-topic">exclusivity:</h2>
                <h2 className="incoming-funko-info">
                  {incoming_funko.funko.exclusivity === ""
                    ? "none"
                    : `${incoming_funko.funko.exclusivity}`}
                </h2>
              </div>
              <div>
                <h2 className="incoming-funko-topic">trending value:</h2>
                <h2 className="incoming-funko-info">
                  {incoming_funko.funko.trending_value == null
                    ? "$--"
                    : `$${incoming_funko.funko.trending_value}`}
                </h2>
              </div>
              <br />
              <div>
                <h2 className="incoming-funko-topic">funko belongs to:</h2>
                <h2 className="incoming-funko-info">
                  {incoming_funko.funko.user.username}
                </h2>
              </div>
              <div>
                <h2 className="incoming-funko-topic">from:</h2>
                <h2 className="incoming-funko-info">
                  {incoming_funko.funko.user.city},{" "}
                  {incoming_funko.funko.user.country}
                </h2>
              </div>
            </div>
          </div>
          <div className="confirm-offer-container">
            <img src={tradeIcon} alt="transfer-logo" className="trade-image" />
            <button onClick={this.makeAnOffer}>confirm offer</button>
            <Link to={"/funkos-to-trade"}>
              <button>see all funkos on trade</button>
            </Link>
          </div>
        </div>
      );
    };

    return this.state.ready ? renderOffer : <div>loading</div>;
  }
}

export default OfferPage;
