import React, { Component } from "react";

class SearchBar extends Component {
  render() {
    return (
      <form className="search-bar">
        <input
          name="searchTerm"
          type="text"
          placeholder="search..."
          onChange={() => this.props.handleChange}
        />
      </form>
    );
  }
}

export default SearchBar;
