import React, { Component } from "react";

class SearchBar extends Component {
  state = {
    search: ""
  };

  handleSearch = e => {
    const searchTerm = e.target.value;
    setTimeout(() => {
      this.setState({
        search: e.target.value
      });
    }, 5);
  };

  render() {
    return (
      <div className="search-bar">
        <input
          name="searchTerm"
          type="text"
          placeholder="search..."
          onChange={this.handleSearch}
        />
      </div>
    );
  }
}

export default SearchBar;
